package main

import (
	"fmt"
	"github.com/smartwalle/inpay/apple"
)

func main() {
	var c = apple.New()
	var trade, info, err = c.VerifyReceipt("1000000885240308", "")
	if err != nil {
		fmt.Println("验证出错:", err)
		return
	}
	if info != nil {
		fmt.Println(trade.Environment, info.TransactionId, info.ProductId)
	}
}
